CREATE TABLE IF NOT EXISTS `tag` (
	`id`	INTEGER NOT NULL,
	`tag`	VARCHAR ( 255 ) NOT NULL,
	PRIMARY KEY(`id`)
);
CREATE TABLE IF NOT EXISTS `recipe_tags` (
	`recipe_id`	INTEGER NOT NULL,
	`tags_id`	INTEGER NOT NULL,
	PRIMARY KEY(`recipe_id`,`tags_id`),
	FOREIGN KEY(`recipe_id`) REFERENCES `recipe`(`id`),
	FOREIGN KEY(`tags_id`) REFERENCES `tag`(`id`)
);
CREATE TABLE IF NOT EXISTS `recipe_nutrient` (
	`id`	INTEGER NOT NULL,
	`cuantity`	REAL NOT NULL,
	`daily_percentage`	REAL NOT NULL,
	`unit`	VARCHAR ( 255 ) NOT NULL,
	`nutrient_id`	INTEGER NOT NULL,
	`recipe_id`	INTEGER NOT NULL,
	PRIMARY KEY(`id`),
	FOREIGN KEY(`nutrient_id`) REFERENCES `nutrient`(`id`),
	FOREIGN KEY(`recipe_id`) REFERENCES `recipe`(`id`)
);
CREATE TABLE IF NOT EXISTS `recipe_ingredient` (
	`id`	INTEGER NOT NULL,
	`weight`	INTEGER NOT NULL,
	`recipe_id`	INTEGER NOT NULL,
	`ingredient_id`	INTEGER NOT NULL,
	PRIMARY KEY(`id`),
	FOREIGN KEY(`recipe_id`) REFERENCES `recipe`(`id`),
	FOREIGN KEY(`ingredient_id`) REFERENCES `ingredient`(`id`)
);
CREATE TABLE IF NOT EXISTS `recipe` (
	`id`	INTEGER NOT NULL,
	`name`	VARCHAR ( 255 ) NOT NULL,
	`url`	VARCHAR ( 255 ) NOT NULL,
	`attribution`	VARCHAR ( 255 ) NOT NULL,
	`duration`	VARCHAR ( 255 ) NOT NULL,
	`calories`	REAL NOT NULL,
	`weight`	REAL NOT NULL,
	`rations`	REAL NOT NULL,
	`difficulty`	VARCHAR ( 255 ) NOT NULL,
	`cuisine`	VARCHAR ( 255 ) NOT NULL,
	`steps`	TEXT,
	`user_id`	INTEGER NOT NULL,
	PRIMARY KEY(`id`)
);
CREATE TABLE IF NOT EXISTS `nutrient` (
	`id`	INTEGER NOT NULL,
	`nutrient`	VARCHAR ( 255 ) NOT NULL,
	PRIMARY KEY(`id`)
);
CREATE TABLE IF NOT EXISTS `ingredient` (
	`id`	INTEGER NOT NULL,
	`name`	VARCHAR ( 255 ) NOT NULL,
	PRIMARY KEY(`id`)
);
CREATE INDEX IF NOT EXISTS `recipetags_tags_id` ON `recipe_tags` (
	`tags_id`
);
CREATE INDEX IF NOT EXISTS `recipetags_recipe_id` ON `recipe_tags` (
	`recipe_id`
);
CREATE INDEX IF NOT EXISTS `recipenutrient_recipe_id` ON `recipe_nutrient` (
	`recipe_id`
);
CREATE INDEX IF NOT EXISTS `recipenutrient_nutrient_id` ON `recipe_nutrient` (
	`nutrient_id`
);
CREATE INDEX IF NOT EXISTS `recipeingredient_recipe_id` ON `recipe_ingredient` (
	`recipe_id`
);
CREATE INDEX IF NOT EXISTS `recipeingredient_ingredient_id` ON `recipe_ingredient` (
	`ingredient_id`
);
