INSERT INTO `recipe` (id,name,url,attribution,duration,calories,weight,rations,difficulty,cuisine,steps,user_id) VALUES (1,'Pasta con salchichas italianas','http://www.comidakraft.com/sp/recipes/pasta-con-salchichas-italianas-144663.aspx','Comida Kraft','35 Min',3569.632988,1516.75867,8.0,'Fácil','Italiana','{"1": "Calienta la parrilla a fuego medio-bajo.", "2": "Mezcla los vegetales; ponlos en una hoja grande de papel aluminio resistente. Rocíalos con 1/4 taza del aderezo. Dobla el papel para armar un paquete.", "3": "Asa el paquete y las salchichas de 15 a 20 min. o hasta que las salchichas estén cocidas; recuerda voltearlas de vez en cuando. Entretanto, cuece la pasta como se indica en el paquete, pero omite la sal.", "4": "Corta las salchichas por la mitad a lo largo y luego, a lo ancho en rebanadas finas; ponlas en una fuente grande. Haz pequeños cortes en el paquete antes de abrirlo para dejar salir el vapor. Añade los vegetales (con su jugo) a las salchichas. Escurre la pasta; agrégala a la fuente con el resto del aderezo. Revuelve hasta mezclar todo bien; espolvorea la pasta con el queso."}',1);
INSERT INTO `recipe` (id,name,url,attribution,duration,calories,weight,rations,difficulty,cuisine,steps,user_id) VALUES (2,'Ensalada italiana','http://www.comidakraft.com/sp/recipes/ensalada-italiana-94949.aspx','Comida Kraft','25 Min',2030.4901114,986.01242,4.0,'Fácil','Italiana','{"1": "Coloca la lechuga en un platón para servir. Coloca los demás ingredientes, excepto el aderezo, sobre la lechuga.", "2": "Rocía la ensalada con el aderezo; mézclala justo antes de servir."}',1);
INSERT INTO `recipe` (id,name,url,attribution,duration,calories,weight,rations,difficulty,cuisine,steps,user_id) VALUES (3,'Frittata a la Italiana','http://www.quericavida.com/recetas/frittata-a-la-italiana/','Que Rica Vida','20 min',813.34325,788.075,4.0,'Fácil','Italiana','{"1": "En un tazón mediano, mezcla los huevos, la salvia, la sal, la pimienta y un cuarto de taza de queso; bate bien. Deja aparte.", "2": "Calienta el aceite en una sartén antiadherente mediana para horno, a fuego medio. Añade la calabacita y las cebollitas; cocina y revuelve 2 minutos o hasta que la calabacita esté blanda. Añade la mezcla de huevo; cocina 2 minutos o hasta que la mezcla de huevo esté casi firme, levantando los bordes ocasionalmente, para permitir que la mezcla de huevo sin cocer fluya al fondo de la sartén.", "3": "Cubre la frittata con rebanadas de tomate; espolvorea con el cuarto de taza restante de queso. Asa a 4 o 6 pulgadas del fuego, de 1 a 3 minutos o hasta que la parte superior esté firme y empiece a dorarse."}',1);
INSERT INTO `recipe` (id,name,url,attribution,duration,calories,weight,rations,difficulty,cuisine,steps,user_id) VALUES (4,'Ensalada de pasta italiana','http://www.comidakraft.com/sp/recipes/ensalada-de-pasta-italiana-152154.aspx','Comida Kraft','2 H 20 Min',2243.07,974.5,8.0,'Fácil','Italiana','{"1": "Combina todos los ingredientes menos el aderezo en un tazón grande.", "2": "Agrega el aderezo; mezcla todo ligeramente.", "3": "Refrigera la ensalada durante varias horas."}',1);
INSERT INTO `recipe` (id,name,url,attribution,duration,calories,weight,rations,difficulty,cuisine,steps,user_id) VALUES (5,'Ensalada primavera a la italiana','http://www.comidakraft.com/sp/recipes/ensalada-primavera-a-la-italiana-64119.aspx','Comida Kraft','15 Min',808.46,705.75,6.0,'Fácil','Italiana','{"1": "Mezcla los 6 primeros ingredientes en un tazón grande. ", "2": "Agrega el aderezo y mezcla ligeramente."}',1);
INSERT INTO `recipe` (id,name,url,attribution,duration,calories,weight,rations,difficulty,cuisine,steps,user_id) VALUES (6,'Ensalada de Col a la Italiana','http://www.quericavida.com/recetas/ensalada-de-col-a-la-italiana/','Que Rica Vida','2 hr',964.15496552,1035.475136,12.0,'Fácil','Italiana','{"1": "En un recipiente hondo chico, mezcla los ingredientes para el aderezo; retira a un lado.", "2": "En un recipiente hondo grande, mezcla los ingredientes para la ensalada; agrega el aderezo y mezcla. Cubre y refrigera aproximadamente dos horas antes de servir para que los sabores se incorporen."}',1);
INSERT INTO `recipe` (id,name,url,attribution,duration,calories,weight,rations,difficulty,cuisine,steps,user_id) VALUES (7,'Pasta a la Italiana con Camarones','http://www.quericavida.com/recetas/pasta-a-la-italiana-con-camarones/','Que Rica Vida','15 min',1493.1001025,830.11475,4.0,'Fácil','Italiana','{"1": "Cuece la pasta como se indica en el paquete. Coloca los camarones en un colador; enjuágalos brevemente con agua fría. Déjalos reposar en el colador, hasta que la pasta se cueza. Para escurrir la pasta, viértela sobre los camarones en el colador.", "2": "Mientras tanto, en un recipiente hondo grande, mezcla los ingredientes restantes. Revuelve con suavidad la pasta cocida y los camarones con la mezcla de tomate. Sirve de inmediato."}',1);
INSERT INTO `recipe` (id,name,url,attribution,duration,calories,weight,rations,difficulty,cuisine,steps,user_id) VALUES (8,'Ejotes y tomates a la italiana','http://www.comidakraft.com/sp/recipes/ejotes-y-tomates-a-la-italiana-91598.aspx','Comida Kraft','15 Min',324.54252,579.642,6.0,'Fácil','Italiana','{"1": "Mezcla todos los ingredientes."}',1);
INSERT INTO `recipe` (id,name,url,attribution,duration,calories,weight,rations,difficulty,cuisine,steps,user_id) VALUES (9,'Ensalada de cuscús a la griega','http://www.comidakraft.com/sp/recipes/ensalada-de-cuscus-a-la-griega-114557.aspx','Comida Kraft','1 H 25 Min',1341.746,690.9,6.0,'Difícil','Griega','{"1": "Cuece el cuscús tal como se indica en el paquete, omitiendo la sal y la mantequilla. Revuélvelo con un tenedor. Ponlo en un tazón grande; déjalo enfriar 10 min.", "2": "Añade los ingredientes restantes; revuelve todo ligeramente.", "3": "Refrigera la ensalada durante 1 hora."}',1);
INSERT INTO `recipe` (id,name,url,attribution,duration,calories,weight,rations,difficulty,cuisine,steps,user_id) VALUES (10,'Ensalada de Pasta a la Griega','http://www.quericavida.com/recetas/ensalada-de-pasta-a-la-griega/','Que Rica Vida','25 min',1963.0882364,1109.09992,12.0,'Difícil','Griega','{"1": "Cocina, escurre y lava la pasta como lo indica el paquete. Mientras tanto, en un recipiente hondo grande, revuelve el paquetito con la mezcla para la pasta con el agua y el aceite.", "2": "Agrega la pasta escurrida y los ingredientes restantes a la mezcla de sazonar. Revuelve con los crutones y el queso parmesano (ambos vienen ya incluidos en la mezcla de pasta para ensalada). Sirve inmediatamente o puedes cubrirla y refrigerarla hasta la hora de servir."}',1);
INSERT INTO `recipe` (id,name,url,attribution,duration,calories,weight,rations,difficulty,cuisine,steps,user_id) VALUES (11,'Rehiletes de Ensalada Griega','http://www.quericavida.com/recetas/rehiletes-de-ensalada-griega/','Que Rica Vida','2 hr',2069.2520002,1071.866668,48.0,'Difícil','Griega','{"1": "En un tazón chico, bate el queso crema y el queso feta, con batidora eléctrica, a velocidad media, hasta tener una mezcla homogénea. Añade el pepino.", "2": "Coloca las tortillas en un platón para horno de microondas o en una toalla de papel. Hornea en el horno de microondas, a temperatura alta, de 10 a 15 segundos, hasta que se suavicen. Unta aproximadamente un cuarto de taza de la mezcla de queso en cada tortilla. Cubre con las aceitunas y los tomates. Acomoda media taza de espinacas en cada tortilla, hasta media pulgada del borde. Enrolla apretadamente cada tortilla; envuelve con envoltura plástica. Refrigera de 2 a 3 horas, para que se mezclen los sabores.", "3": "Recorta los bordes de las tortillas enrolladas, si lo deseas. Corta cada rollo en ocho rebanadas de 1 pulgada. Acomoda las rebanadas en un platón, con los lados cortados hacia abajo."}',1);
INSERT INTO `recipe` (id,name,url,attribution,duration,calories,weight,rations,difficulty,cuisine,steps,user_id) VALUES (12,'Arroz con pollo a la griega','http://www.comidakraft.com/sp/recipes/arroz-con-pollo-a-la-griega-63329.aspx','Comida Kraft','40 Min',3422.9702975,2264.41765,4.0,'Difícil','Griega','{"1": "Calienta 1/4 taza del aderezo en un sartén grande a fuego medio. Agrega los vegetales; cocina y revuelve durante 15 minutos o hasta que estén tiernos y crujientes. Retira los vegetales del sartén; tápalos para mantenerlos tibios. ", "2": "Agrega el 1/4 taza de aderezo restante al sartén. Agrega el pollo y cocina cada lado durante 7 minutos o hasta que esté bien cocido. Vuelve a colocar los vegetales en el sartén. Agrega las aceitunas; revuelve suavemente para mezclar bien. ", "3": "Sirve el pollo y los vegetales sobre el arroz."}',1);
INSERT INTO `recipe` (id,name,url,attribution,duration,calories,weight,rations,difficulty,cuisine,steps,user_id) VALUES (13,'“Pastelitos” de Carne a la Griega','http://www.quericavida.com/recetas/pastelitos-de-carne-a-la-griega/','Que Rica Vida','60 min',1235.0027752,1116.318305,4.0,'Difícil','Griega','{"1": "Precalienta el horno a 350°F. Rocía con spray para cocinar 8 moldes para panquecitos de tamaño regular.", "2": "En un recipiente hondo mediano, mezcla los ingredientes para los pasteles de carne y coloca 1/3 de taza en cada molde. Presiona un poco.", "3": "Hornea de 30 a 35 minutos o hasta que un termómetro marque 160° insertado en el centro de la carne. Déjalos en los moldes por 5 minutos.", "4": "Mientras tanto, en un platito mezcla los ingredientes para la salsa tzatziki. Sirve sobre los pastelitos de carne."}',1);
INSERT INTO `recipe` (id,name,url,attribution,duration,calories,weight,rations,difficulty,cuisine,steps,user_id) VALUES (14,'Huachinango asado estilo griego','http://www.comidakraft.com/sp/recipes/huachinango-asado-estilo-griego-171189.aspx','Comida Kraft','50 Min',2198.49976,1495.842,4.0,'Difícil','Griega','{"1": "Coloca el pescado en un molde grande poco profundo. Corta las calabacitas por la mitad a lo largo y, a continuación, cada pedazo por la mitad a lo ancho. Coloca los pedacitos alrededor del pescado. Vierte la vinagreta sobre el pescado y las calabacitas. Ponlos a marinar 30 min.", "2": "Engrasa la parrilla y caliéntala a fuego medio-alto. Escurre el pescado y las calabacitas; deshazte de la marinada.", "3": "Asa el pescado y las calabacitas de 5 a 6 min. por lado o hasta que el pescado se desmenuce fácilmente con un tenedor y las calabacitas estén tiernas pero crujientes. Sírvelos sobre el arroz."}',1);
INSERT INTO `recipe` (id,name,url,attribution,duration,calories,weight,rations,difficulty,cuisine,steps,user_id) VALUES (15,'Ensalada Griega de Garbanzos y Pepino','http://www.quericavida.com/recetas/ensalada-griega-de-garbanzos-y-pepino/','Que Rica Vida','30 min',729.948,1124.325,8.0,'Difícil','Griega','{"1": "En un recipiente hondo grande, mezcla los garbanzos, jitomates, pimientos, cebolla, pepino, cilantro y ajo.", "2": "En un platito mezcla el jugo de limón, aceite y sal hasta que se incorporen. Agrega a la ensalada de garbanzos y mezcla hasta que todo el aderezo se incorpore. Antes de servir, agrega el queso."}',1);
INSERT INTO `recipe` (id,name,url,attribution,duration,calories,weight,rations,difficulty,cuisine,steps,user_id) VALUES (16,'Ensalada mediterránea de cebada','http://www.comidakraft.com/sp/recipes/ensalada-mediterranea-de-cebada-112810.aspx','Comida Kraft','1 H 35 Min',1528.44434112,2483.833336,8.0,'Difícil','Griega','{"1": "Hierve el agua en una cacerola grande a fuego medio-alto. Agrega la cebada y revuelve. Hiérvela de nuevo y mantén un hervor suave a fuego medio-bajo durante 45 min. o hasta que esté tierna la cebada. Escúrrela; déjala enfriar.", "2": "Coloca la cebada en un tazón grande. Agrega los ingredientes restantes; mézclalos un poco."}',1);
INSERT INTO `recipe` (id,name,url,attribution,duration,calories,weight,rations,difficulty,cuisine,steps,user_id) VALUES (17,'Sartén de papas estilo nachos','http://www.comidakraft.com/sp/recipes/sarten-de-papas-estilo-nachos-112593.aspx','Comida Kraft','31 Min',2977.2491,1404.638,6.0,'Difícil','Mexican','{"1": "Calienta 1 cucharada de aceite en una sartén grande. Cocina las papas en tandas de una sola capa durante 4 min por lado o hasta que estén doradas por ambas caras. Saca las papas de la sartén; tápalas para mantenerlas calientes. Repite esto con el aceite y las papas restantes.", "2": "Vuelve a poner las papas en la sartén; cúbrelas con los frijoles y el queso. Tápalas; cocina esto 5 min o hasta que los frijoles estén bien calientes y el queso, derretido.", "3": "Corona las papas con los chiles y la crema agria."}',1);
INSERT INTO `recipe` (id,name,url,attribution,duration,calories,weight,rations,difficulty,cuisine,steps,user_id) VALUES (18,'Sopa mexicana de tortilla','http://www.comidakraft.com/sp/recipes/sopa-mexicana-de-tortilla-62508.aspx','Comida Kraft','20 Min',3418.5161335,2617.88423,8.0,'Difícil','Mexican','{"1": "Dora la carne en un sartén grande y escúrrela. ", "2": "Añade la salsa, el caldo, el maíz y pon a hervir. Reduce a fuego lento y cocina por 5 minutos o hasta que se caliente bien. ", "3": "Sirve la sopa cubierta con totopos y queso."}',1);
INSERT INTO `recipe` (id,name,url,attribution,duration,calories,weight,rations,difficulty,cuisine,steps,user_id) VALUES (19,'Macarrones al horno con tres quesos','http://www.comidakraft.com/sp/recipes/macarrones-al-horno-con-tres-quesos-75942.aspx','Comida Kraft','25 Min',2018.3321,840.83,4.0,'Difícil','Mexican','{"1": "Calienta el horno a 350°F.", "2": "Mezcla las galletas trituradas, el queso parmesano y 1/4 taza del queso mexicano. ", "3": "Prepara los macarrones con queso como indica el paquete. Incorpora el pavo, el elote y el restante queso mexicano. Coloca esta mezcla en una fuente para hornear rociada con aceite en aerosol de 1-1/2 cuartillo de capacidad. Cúbrela con la mezcla de galletas. ", "4": "Hornea los macarrones durante 10 min. o hasta que el queso se haya derretido y la superficie esté dorada."}',1);
INSERT INTO `recipe` (id,name,url,attribution,duration,calories,weight,rations,difficulty,cuisine,steps,user_id) VALUES (20,'Horneado trasnochado de tortillas estilo Tex-Mex','http://www.comidakraft.com/sp/recipes/horneado-trasnochado-de-tortillas-estilo-tex-mex-160160.aspx','Comida Kraft','13 H 15 Min',4067.36582,1823.392,10.0,'Difícil','Mexican','{"1": "Forma capas con la mitad de las tiritas de tortilla, de salchichón y de queso en una fuente para hornear de 13x9 pulgs., rociada con aceite en aerosol. Repite las capas.", "2": "Bate bien los huevos con la leche, los chiles, el ajo en polvo, el comino y la pimienta; vierte la mezcla sobre los ingredientes en la fuente para hornear. Espolvorea esto con la páprika y refrigéralo toda la noche.", "3": "Calienta el horno a 350ºF. Esparce los tomates sobre los ingredientes de la cazuela. Hornéala, destapada, de 45 a 50 min. o hasta que el centro esté firme y los bordes ligeramente dorados."}',1);
INSERT INTO `recipe` (id,name,url,attribution,duration,calories,weight,rations,difficulty,cuisine,steps,user_id) VALUES (21,'Tacos suaves de res con salsa BBQ','http://www.comidakraft.com/sp/recipes/tacos-suaves-de-res-con-salsa-bbq-112137.aspx','Comida Kraft','20 Min',2041.5196,1134.792,4.0,'Difícil','Mexican','{"1": "Dora la carne con la cebolla y el pimiento en una sartén antiadherente. Incorpora la salsa; cocina por 2 min. o hasta que se haya calentado totalmente, revolviendo de vez en cuando.", "2": "Coloca esta mezcla sobre las tortillas; dóblalas por la mitad.", "3": "Cubre los tacos con el tomate y el queso."}',1);
INSERT INTO `recipe` (id,name,url,attribution,duration,calories,weight,rations,difficulty,cuisine,steps,user_id) VALUES (22,'Sartén de macarrones con queso, carne de res y salsa','http://www.comidakraft.com/sp/recipes/sarten-de-macarrones-con-queso-carne-de-res-y-salsa-108672.aspx','Comida Kraft','30 Min',2858.655725,1549.3295,4.0,'Difícil','Mexican','{"1": "Dora la carne en una sartén grande; escúrrela.", "2": "Añade el agua, la salsa y los macarrones; revuelve. Déjalos hervir; tapa la sartén. Mantén un hervor suave durante 10 min. o hasta que los macarrones estén blandos, revolviendo de vez en cuando. Agrega los granos de elote y el polvo de queso del paquete; revuelve. Cocina el conjunto 2 min. o hasta que se termine de calentar.", "3": "Completa el platillo con el queso desmenuzado y las cebollitas."}',1);
INSERT INTO `recipe` (id,name,url,attribution,duration,calories,weight,rations,difficulty,cuisine,steps,user_id) VALUES (23,'Chiles bananos rellenos con camarones cremosos','http://www.comidakraft.com/sp/recipes/chiles-bananos-rellenos-con-camarones-cremosos-114653.aspx','Comida Kraft','40 Min',3469.266138,1939.6198,12.0,'Difícil','Mexican','{"1": "Calienta el horno a 350ºF.", "2": "Licúa la salsa y el queso crema hasta que queden cremosos; vierte la mezcla en una fuente para hornear de 13x9 pulgadas cubriendo parejamente su fondo.", "3": "Mezcla los camarones, el maíz, el queso y 1/2 taza de cebollitas; rellena cada chile con 1/3 taza de mezcla de camarones. Pon los chiles en la fuente alineados en una sola capa. Tapa la fuente con papel aluminio.", "4": "Hornéalos durante 25 min. o hasta que estén bien calientes. Corónalos con las cebollitas restantes."}',1);
INSERT INTO `recipe` (id,name,url,attribution,duration,calories,weight,rations,difficulty,cuisine,steps,user_id) VALUES (24,'Pollo a los tres quesos estilo sudoeste','http://www.comidakraft.com/sp/recipes/pollo-a-los-tres-quesos-estilo-sudoeste-136191.aspx','Comida Kraft','40 Min',2450.45844176,1096.8807044,6.0,'Difícil','Mexican','{"1": "Calienta el horno a 375ºF.", "2": "Combina la mezcla para empanizar y los condimentos en un tazón mediano e incorpora el queso.", "3": "Humedece las pechugas con agua. Cúbrelas una por una con la mezcla de queso. Presiona ligeramente la mezcla de queso contra ambos lados de las pechugas. (No quedarán completamente cubiertas). Colócalas en una charola (bandeja) para hornear rociada con aceite en aerosol y ponles encima la mezcla de queso restante.", "4": "Hornéalas de 28 a 30 min. o hasta que estén cocidas (temperatura interna de 165ºF)."}',1);
INSERT INTO `recipe` (id,name,url,attribution,duration,calories,weight,rations,difficulty,cuisine,steps,user_id) VALUES (25,'Ensalada de carne de cerdo con jalapeños','http://www.comidakraft.com/sp/recipes/ensalada-de-carne-de-cerdo-con-jalapenos-132495.aspx','Comida Kraft','25 Min',3527.93334112,1925.333336,8.0,'Difícil','Mexican','{"1": "Combina en un tazón grande todos los ingredientes excepto el aguacate y las tostadas.", "2": "Pon el aguacate encima.", "3": "Sirve esta mezcla con las tostadas."}',1);
INSERT INTO `recipe` (id,name,url,attribution,duration,calories,weight,rations,difficulty,cuisine,steps,user_id) VALUES (26,'Frittata de repollo','http://www.comidakraft.com/sp/recipes/frittata-de-repollo-126702.aspx','Comida Kraft','25 Min',1317.78,1056.75,6.0,'Difícil','Mexican','{"1": "Calienta el aceite en una sartén grande a fuego medio-alto. Añade la cebolla y los chiles; cocínalos durante 1 min., revolviendo de vez en cuando. Incorpora la col y los tomates; cocínalos durante 5 min. o hasta que la col se ablande, revolviendo de vez en cuando.", "2": "Bate los huevos; viértelos sobre la mezcla de col. Tapa la sartén y cocina todo a fuego medio-bajo durante 8 min. o hasta que el centro de la frittata esté firme.", "3": "Ponle el queso encima; deja reposar tapada la frittata durante 2 min. o hasta que el queso se derrita. Sírvela con las tortillas."}',1);
INSERT INTO `recipe` (id,name,url,attribution,duration,calories,weight,rations,difficulty,cuisine,steps,user_id) VALUES (27,'Tortitas de garbanzos con salsa de tomate','http://www.comidakraft.com/sp/recipes/tortitas-de-garbanzos-con-salsa-de-tomate-112353.aspx','Comida Kraft','30 Min',1595.65368,2102.582,6.0,'Difícil','Mexican','{"1": "Procesa ligeramente los garbanzos, el huevo, las galletas y ½ cebolla en una procesadora de alimentos. Transfiere esto a un tazón; incorpora el queso. Forma 12 tortitas, usando cerca de ¼ taza de mezcla de garbanzos en cada una. ", "2": "Calienta una sartén grande antiadherente, rociada con aceite en aerosol, a fuego medio-alto. Añade la mitad de las tortitas; cocínalas 5 min. por lado o hasta que estén crujientes y doradas por ambos lados. Sácalas de la sartén; tápalas. Repite con el resto de las tortitas. ", "3": "Entretanto, calienta otra sartén grande a fuego medio-alto. Rebana la cebolla restante. Ponla en la sartén; cocínala por 5 min. o hasta que esté tierna pero crujiente, revolviendo frecuentemente. Agrega la salsa de tomate y el palito de canela. Deja que hierva; cocina esto a fuego medio-bajo durante 5 min., revolviendo de vez en cuando. Deshazte del palito de canela. Cubre las tortitas con la salsa; espolvoréalas con el cilantro."}',1);
INSERT INTO `recipe` (id,name,url,attribution,duration,calories,weight,rations,difficulty,cuisine,steps,user_id) VALUES (28,'Empanadas de pollo con queso','http://www.comidakraft.com/sp/recipes/empanadas-de-pollo-con-queso-120910.aspx','Comida Kraft','40 Min',2909.3299742,1103.905195,16.0,'Difícil','Mexican','{"1": "Calienta el horno a 375ºF.", "2": "Mezcla los 4 primeros ingredientes. Bate bien el huevo con el agua con un batidor de varillas. ", "3": "Desenrolla la masa sobre una superficie ligeramente enharinada. Usa un rodillo para estirar cada base hasta formar círculos de 12 pulgs. Corta cada base en 8 círculos (4 pulgs.) con un cortador para panecillos, volviendo a enrollar la masa si fuera necesario. Colócalos en charolas (bandejas) para hornear cubiertas con papel para hornear (parchment paper). ", "4": "Pon 2 cdas. de mezcla de pollo al centro de cada círculo; barniza el borde con el huevo. Dobla los círculos de masa por la mitad. Presiónalos ligeramente para sacar el exceso de aire; sella los bordes con un tenedor. Barniza las empanadas con el huevo restante. Espolvoréalas con el chile en polvo. ", "5": "Hornéalas 15 min. o hasta que estén bien doraditas."}',1);
INSERT INTO `recipe` (id,name,url,attribution,duration,calories,weight,rations,difficulty,cuisine,steps,user_id) VALUES (29,'Enfrijoladas con pollo','http://www.comidakraft.com/sp/recipes/enfrijoladas-con-pollo-125153.aspx','Comida Kraft','23 Min',4788.750273,1819.4753,6.0,'Difícil','Mexican','{"1": "Mezcla los frijoles y la mitad de las hojas de epazote en la licuadora hasta que estén homogéneos. Pon la mezcla en una sartén. Cocina a fuego medio durante 8 min. o hasta que esté completamente caliente, revolviendo de vez en cuando. Incorpora la crema agria.", "2": "Coloca 1/4 taza de pollo sobre el centro de cada tortilla; enróllalas. Colócalas con la juntura hacia abajo, en una sola capa, en una fuente para servir. ", "3": "Pon encima la mezcla de frijoles, el queso, el chorizo, el resto del epazote y la cebolla."}',1);
INSERT INTO `recipe` (id,name,url,attribution,duration,calories,weight,rations,difficulty,cuisine,steps,user_id) VALUES (30,'Tostadas de pollo y queso','http://www.comidakraft.com/sp/recipes/tostadas-de-pollo-y-queso-74845.aspx','Comida Kraft','18 Min',2202.25131,1231.512175,6.0,'Difícil','Mexican','{"1": "Calienta el horno a 400°F.", "2": "Coloca las tortillas en una sola capa sobre una charola (bandeja) para hornear grande; rocía las tortillas con aceite en aerosol. Hornéalas de 7 a 8 min. o hasta que estén crujientes. ", "3": "Cocina, entretanto, el pollo una sartén grande antiadherente rociado con aceite en aerosol a fuego medio-alto durante 5 min. o hasta que esté cocido. Agrega la salsa y el chile en polvo; cocínalo todo hasta que esté caliente, revolviendo de vez en cuando. Retira el sartén del fuego; agrega el queso y revuelve. ", "4": "Coloca con una cuchara la mezcla con pollo sobre las tortillas; pon encima los ingredientes restantes."}',1);
INSERT INTO `recipe` (id,name,url,attribution,duration,calories,weight,rations,difficulty,cuisine,steps,user_id) VALUES (31,'Rápida sartén de carne de res con sabor a burritos','http://www.comidakraft.com/sp/recipes/rapida-sarten-de-carne-de-res-con-sabor-a-burritos-111145.aspx','Comida Kraft','30 Min',3397.4135031,1841.12117,4.0,'Difícil','Mexican','{"1": "Dora la carne en una sartén antiadherente grande a fuego medio-alto; escúrrela. ", "2": "Agrega los próximos 4 ingredientes; revuelve. Deja que hierva; mantén un hervor suave a fuego medio-bajo por 5 min.", "3": "Incorpora los pedazos de tortillas; espolvoréale el queso. Retira la sartén del fugego; tápala. Déjala reposar 5 min. o hasta que se derrita el queso. Cubre con la crema agria y las cebollas."}',1);
INSERT INTO `recipe` (id,name,url,attribution,duration,calories,weight,rations,difficulty,cuisine,steps,user_id) VALUES (32,'Cazuela de pollo a la mexicana','http://www.comidakraft.com/sp/recipes/cazuela-de-pollo-a-la-mexicana-112112.aspx','Comida Kraft','45 Min',3492.49718,1748.844,4.0,'Difícil','Mexican','{"1": "Calienta el horno a 375ºF.", "2": "Cocina, revolviendo a fuego medio por 2 min., el pollo con el comino en una sartén antiadherente, rociada con aceite en aerosol. Añade el pimiento; cocínalo 2 min., revolviendo de vez en cuando. Incorpora la salsa; cocina por 2 min. Agrega el queso neufchatel; cocínalo 2 min. o hasta que el queso se derrita. Incorpora los frijoles y los tomates.", "3": "Pon 1/3 de la mezcla de pollo en una fuente para hornear cuadrada de 8 pulgadas rociado con aceite en aerosol; cúbrela con 1 tortilla y la mitad de la mezcla de pollo restante y la mitad del queso. Cubre esto con la tortilla restante y la mezcla de pollo; tápalo.", "4": "Hornea 20 min. o hasta que esté completamente caliente. Espolvoréalo con el resto del queso desmenuzado. Hornéalo, destapado, por 5 min. o hasta que el queso se derrita."}',1);
INSERT INTO `recipe` (id,name,url,attribution,duration,calories,weight,rations,difficulty,cuisine,steps,user_id) VALUES (33,'Horneado de tortillas en capas','http://www.comidakraft.com/sp/recipes/horneado-de-tortillas-en-capas-107454.aspx','Comida Kraft','1 H 35 Min',5209.44204,2701.40905,8.0,'Difícil','Mexican','{"1": "Calienta el horno a 375ºF.", "2": "Dora la carne con la cebolla en una sartén grande a fuego medio-alto; escurre la carne. Agrega los próximos 4 ingredientes; mézclalos bien.", "3": "Consigue dos fuentes para hornear cuadradas de 8 pulgs. y acomoda 2 tortillas, sin amontonarlas, en la base de cada una; forma capas sobre cada fuente utilizando un cuarto de la mezcla de carne, un cuarto de la crema agria y un cuarto del queso. Repite todas las capas. Cúbrelas con papel aluminio. Congela una de las fuentes por un máximo de 3 meses.", "4": "Hornea la otra fuente, sin destaparla, durante 30 min. Retira el papel aluminio. Hornea la fuente por otros 10 min. o hasta que el horneado esté bien caliente y el queso se haya derretido. Deja reposar el horneado 5 min. antes de cortarlo para servir."}',1);
INSERT INTO `recipe` (id,name,url,attribution,duration,calories,weight,rations,difficulty,cuisine,steps,user_id) VALUES (34,'Lasaña de pollo y salsa verde','http://www.comidakraft.com/sp/recipes/lasana-de-pollo-y-salsa-verde-167557.aspx','Comida Kraft','1 H 5 Min',5154.678,2545.2,9.0,'Difícil','Mexican','{"1": "Calienta el horno a 375°F.", "2": "Mezcla bien el queso crema con el huevo en un tazón mediano. Incorpora el queso ricotta y 1/4 taza de cilantro; reserva esta mezcla. Combina el pollo y 1-1/2 taza de salsa.", "3": "Esparce 3/4 taza del resto de la salsa en el fondo de un molde para hornear de 13x9 pulgs. que hayas rociado con aceite en aerosol; ponle una capa de 3 láminas, otra capa con la mitad de la mezcla de queso ricotta, otra con la mitad de la mezcla de pollo y otra con 1/2 taza de queso desmenuzado. Repite las capas. Pon el resto de las láminas, de la salsa y del queso desmenuzado; tapa la lasaña.", "4": "Hornéala 35 min. o hasta que se termine de calentar; no te olvides de destaparla 5 min. antes de terminar. Déjala reposar 10 min. Ponle el resto del cilantro y los tomates."}',1);
INSERT INTO `recipe` (id,name,url,attribution,duration,calories,weight,rations,difficulty,cuisine,steps,user_id) VALUES (35,'Horneado cremoso de maíz, calabacitas y chipotle','http://www.comidakraft.com/sp/recipes/horneado-cremoso-de-maiz-calabacitas-y-chipotle-95017.aspx','Comida Kraft','40 Min',2828.734,1535.1,8.0,'Difícil','Mexican','{"1": "Calienta el horno a 350°F.", "2": "Calienta el aderezo en una sartén grande a fuego medio-alto. Agrega la cebolla y el tocino; cocínalos y revuélvelos 5 min. o hasta que la cebolla esté tierna pero crujiente. Añade los calabacitas; cocínalos, revolviendo de vez en cuando, de 8 a 10 min. o hasta que estén ligeramente dorados. Retira la sartén del fuego; escurre el líquido. Agrega el elote, 1 taza de queso, la crema agria y el chile; mézclalos bien.", "3": " Coloca esta mezcla en un molde para hornear de 2 litros; ponle el resto del queso por encima.", "4": "Hornea el molde 15 min. o hasta que el queso esté derretido y se haya calentado bien la mezcla; revuelve. Espolvorea el horneado con el cilantro."}',1);
INSERT INTO `recipe` (id,name,url,attribution,duration,calories,weight,rations,difficulty,cuisine,steps,user_id) VALUES (36,'Cremoso chili blanco con pollo','http://www.comidakraft.com/sp/recipes/cremoso-chili-blanco-con-pollo-124197.aspx','Comida Kraft','25 Min',3171.31324,1685.792,6.0,'Difícil','Mexican','{"1": "Cocina el pollo y la cebolla, revolviendo con frecuencia, en una cacerola grande rociada con aceite en aerosol a fuego medio de 8 a10 min. o hasta que esté el pollo. ", "2": "Mezcla bien el queso crema, la leche y los condimentos. Agrega esta mezcla a la mezcla con pollo junto con los frijoles, el caldo y los chiles; mezcla todo bien. Mantén un hervor suave a fuego medio-bajo durante 5 min. o hasta que se termine de calentar, revolviendo de vez en cuando.", "3": "Sirve el chili con los ingredientes restantes por encima."}',1);
INSERT INTO `recipe` (id,name,url,attribution,duration,calories,weight,rations,difficulty,cuisine,steps,user_id) VALUES (37,'Sopes de hongos','http://www.comidakraft.com/sp/recipes/sopes-de-hongos-123294.aspx','Comida Kraft','50 Min',2739.11848,2316.484,10.0,'Difícil','Mexican','{"1": "Calienta el aceite en una sartén grande a fuego medio-alto. Agrega las cebollas, los chiles y el ajo; cocina durante 2 min. revolviendo frecuentemente. Añade y revuelve los champiñones; cocina durante 15 min. o hasta que se desprenda todo el jugo, revolviendo de vez en cuando. Agrega el jamón; revuelve. Ponle la tapa para mantenerlo calientito.", "2": "Mientras tanto, mezcla la masa harina, la sal y el agua hasta que la mezcla forme una masa suave. Divídela en 10 porciones; aplana cada una de ellas en discos de 5 pulgadas. Calienta una sartén grande a fuego medio-alto. Cocina los sopes, en tandas, durante 2 min. o hasta que estén ligeramente tostados en ambos lados, volteándolos cada 30 segundos. Utiliza tu dedo pulgar e índice para realizar un borde de 1/4 pulgada de alto alrededor de los sopes.", "3": "Rellénalos con la mezcla de champiñones y espolvoréalos con queso."}',1);
INSERT INTO `recipe` (id,name,url,attribution,duration,calories,weight,rations,difficulty,cuisine,steps,user_id) VALUES (38,'Tapa de Pimientos Piquillo Asados','http://www.quericavida.com/recetas/pollo-en-fricase-con-cerveza-y-nopalitos/','Que Rica Vida','60 min',1031.286204,462.53424062688,6.0,'Medio','Española','{"1": "En una cazuela grande agrega el aceite de oliva y sancocha la cebolla y el ajo. Agrega el pollo y combina. Después agrega los tomates enlatados, los pimientos piquillo cortados, el chipotle, los cubitos de pollo, el comino y la pimienta. Revuelve bien con una cuchara de madera y agrega las hojitas de laurel y la cerveza. Tapa la cazuela y deja que se cocine a fuego medio. Prueba el guisado y si le hace falta sal agrega al gusto.  Entre mas tiempo dejes el guisado cocinándose mejor será el sabor. Agrega los nopalitos al final y cocina por otros minutos nuevamente tapando la cazuela. Para servir puedes acompañar de frijoles negros y arroz blanco."}',1);
INSERT INTO `recipe` (id,name,url,attribution,duration,calories,weight,rations,difficulty,cuisine,steps,user_id) VALUES (39,'Tortilla Española','http://www.quericavida.com/recetas/tortilla-espanola/','Que Rica Vida','60 min',1632.92,904.0,8.0,'Difícil','Española','{"1": "Vierte 1/4 de taza del aceite de oliva en la sartén a fuego medio. Cuando esté caliente, agrega las cebollas y las papas y comienza a freír. Condimenta con la sal, aproximadamente 3/4 de cucharadita. Prueba para asegurarte de que esté a tu gusto.", "2": "Deja que las papas se cocinen totalmente. Ahora presta atención porque esta es la parte más importante de todo el proceso de fritura. A medida que se cocinen las papas, aplasta un poco los extremos con un utensilio de cocina, ¡pero no las hagas puré! Debes seguir teniendo trozos de papa a la vista del tamaño de un bocado. Cuando las papas y las cebollas estén listas, transfiérelas a un recipiente grande y deja que se enfríen un poco.", "3": "Bate los huevos y agrega 1/4 cucharadita de sal.", "4": "Mezcla los huevos con las papas y las cebollas. Bate bien. Todas las papas deben quedar cubiertas aunque sin sumergirse en el huevo. Debes tener un poco de huevo adicional en la parte superior, por lo tanto, si 5 huevos no son suficientes, agrega uno más.", "5": "En otra sartén, agrega 1/4 taza de aceite de oliva a fuego entre bajo y medio. Cuando esté caliente, vierte la mezcla de huevos, papas y cebollas, aplánala y NO LA VUELVAS A TOCAR. Deja que se cocine.", "6": "Toma un plato grande y colócalo encima de la sartén. Sujétalo firmemente con la mano (no olvides de usar un guante, ten cuidado). Toma el mango de la sartén con la mano que tienes libre y da vuelta la mitad de la tortilla cocida sobre el plato. Este movimiento debe ser rápido. Coloca la sartén nuevamente en el fuego y vuelve a colocar con cuidado la tortilla con el lado sin cocinar hacia abajo. Deja que ese lado también se cocine.", "7": "Cuando puedas hacer deslizar un poco la tortilla en la sartén, eso significa que está lista para transferirla a un plato. Apaga el fuego, toma un segundo plato y da vuelta la tortilla sobre el plato.", "8": "¡Buen provecho!"}',1);
INSERT INTO `recipe` (id,name,url,attribution,duration,calories,weight,rations,difficulty,cuisine,steps,user_id) VALUES (40,'Mejillones a la española','http://www.comidakraft.com/sp/recipes/mejillones-a-la-espanola-111613.aspx','Comida Kraft','24 Min',1700.38536,1935.551,12.0,'Difícil','Española','{"1": "Calienta el aceite de oliva en una olla grande pesada o en una de hierro tipo “Dutch oven” a fuego medio. Agrega el jamón, la cebolla y el ajo; cocínalos durante 5 minutos o hasta que estén ligeramente dorados y blandos. ", "2": "AÑADE el caldo; deja que hierva. Incorpora los mejillones; tapa la olla. Cocina de 4 a 5 minutos o hasta que los mejillones se hayan abierto. (Desecha los mejillones que no se hayan abierto.) ", "3": "RETIRA los mejillones del fuego. Incorpora la crema agria y el perejil; mezcla bien. Sírvelos inmediatamente."}',1);
INSERT INTO `recipe` (id,name,url,attribution,duration,calories,weight,rations,difficulty,cuisine,steps,user_id) VALUES (41,'Tortilla de papas a la española','http://www.comidakraft.com/sp/recipes/tortilla-de-papas-a-la-espanola-56156.aspx','Comida Kraft','1 H 5 Min',184.176,118.8,12.0,'Difícil','Española','{"1": "Calienta a fuego medio 1/4 taza del aceite en un sartén refractario de 8 ó 9 pulgadas. Agrega 1/3 de las papas; cúbrelas con capas formadas por 1/3 de la cebolla y el ajo, respectivamente. Espolvorea ligeramente con sal y pimienta. Repite las capas 2 veces más. Cocina durante 5 minutos. Voltea cuidadosamente la mezcla de papas; y cocina durante 5 minutos más. Saca la mezcla de papas del sartén y colócala en un colador. Escúrrela y lava el sartén. ", "2": "Precalienta el asador del horno. Bate los huevos en un tazón grande. Agrégales la mezcla de papas; revuelve ligeramente. Deja reposar durante 10 minutos. Calienta a fuego medio la cucharada de aceite restante en el mismo sartén. Agrega la mezcla de huevos; inclina el sartén para cubrir parejamente su fondo. Reduce el fuego a medio-bajo. Cocina durante 2 minutos y de vez en cuando, levanta suavemente la tortilla para evitar que se pegue al sartén.", "3": "Pon el sartén en el horno a una distancia de 4 a 6 pulgadas del fuego. Ásalo durante 10 minutos o hasta que el centro esté firme y la superficie ligeramente dorada. Despega la tortilla del borde del sartén con mucho cuidado; deslízala sobre un platón para servir. Córtala en cuñas o cuadritos del tamaño de un bocado. Sírvela caliente con palillos para pinchar."}',1);
INSERT INTO `recipe` (id,name,url,attribution,duration,calories,weight,rations,difficulty,cuisine,steps,user_id) VALUES (42,'Sopa Española Verde de Almejas','http://www.quericavida.com/recetas/sopa-espanola-verde-de-almejas/','Que Rica Vida','30 min',1381.788168,1804.5598,6.0,'Difícil','Española','{"1": "En una olla de 5 cuartos, calienta el aceite a fuego medio alto. Agregar la cebolla y el puerro. Cocinar 2 a 4 minutos, agitar con frecuencia, hasta que la cebolla esté translúcida. Agrega el ajo, el perejil y el cilantro; cocinar entre 30 segundos y 1 minuto. Añade las migas de pan y revuelve frecuentemente hasta que se unan y se vuelvan ligeramente doradas. Agrega el vino y deja que se evapore, luego añade el caldo. Agrega las almejas.", "2": "Calienta hasta que hierva. Baja el calor a fuego medio. Tapa; sofríe de 5 a 10 minutos o hasta que las almejas se abran por completo (descarta todas las almejas cerradas). Agregar la sal y la pimienta."}',1);
INSERT INTO `recipe` (id,name,url,attribution,duration,calories,weight,rations,difficulty,cuisine,steps,user_id) VALUES (43,'Nuestra favorita paella española','http://www.comidakraft.com/sp/recipes/nuestra-favorita-paella-espanola-162951.aspx','Comida Kraft','1 H 15 Min',3751.784686,2583.2779,12.0,'Difícil','Española','{"1": "Calienta el horno a 325ºF.", "2": "Cocina el caldo y los hilos de azafrán en una cacerola pequeña a fuego medio hasta que empiecen a hervir suavemente.", "3": "Mientras tanto, cocina y revuelve el chorizo y el tocino en una sartén grande refractaria a fuego medio de 4 a 5 min. o hasta que estén listos. Retira la carne de la sartén con una cuchara ranurada; escúrrela en papel toalla.", "4": "Agrega los tomates, el pimiento, la cebolla, el ajo y el aderezo a la grasa en la sartén; cocínalos y revuélvelos por 5 min. Incorpora la mezcla de caldo; déjalo hervir suavemente. Añade el arroz, la carne, los camarones y la tilapia; revuelve todo. Ponle los mejillones encima acomodándolos en la mezcla de arroz; tapa la sartén.", "5": "Hornea la paella de 25 a 30 min. o hasta que se absorba el líquido y los mejillones se abran. Desecha los mejillones sin abrir. Cubre la paella con los chícharos."}',1);
INSERT INTO `recipe` (id,name,url,attribution,duration,calories,weight,rations,difficulty,cuisine,steps,user_id) VALUES (44,'Pollo a la Barbecue','http://www.quericavida.com/recetas/pollo-a-la-barbecue/','Que Rica Vida','20 min',784.0405,716.84,4.0,'Difícil','Española','{"1": "Precalienta la parrilla de gas o de carbón (grill). En un tazón chico, mezcla el azúcar morena, la paprika, el comino, la sal de ajo y el chile en polvo. Frota ambos lados del pollo con la mezcla para sazonar.", "2": "Coloca el pollo en la parrilla. Tapa la parrilla; cocina a fuego medio de 8 a 10 minutos, volteando el pollo una vez, hasta que el jugo del pollo esté claro al hacer un corte en el centro de la parte más gruesa (170°F).", "3": "Sirve el pollo con la salsa para barbecue."}',1);
INSERT INTO `recipe` (id,name,url,attribution,duration,calories,weight,rations,difficulty,cuisine,steps,user_id) VALUES (45,'Sopa crema de ajo estilo español','http://www.comidakraft.com/sp/recipes/sopa-crema-de-ajo-estilo-espanol-91767.aspx','Comida Kraft','20 Min',1353.87675,1820.425,8.0,'Difícil','Española','{"1": "Calienta el aceite en una cacerola grande a fuego medio-alto. Agrega el jamón y el ajo; cocínalos de 1 a 2 min. o hasta que el ajo esté dorado, revolviéndolos constantemente. ", "2": "Agrega el pimentón y el comino. Cocina esto 1 min. o justo hasta que las especias se tuesten.", "3": "Agrega el queso crema y revuélvelo 1 a 2 min. o hasta que esté derretido y bien combinado. Agrega el caldo; revuelve. Déjalo hervir y mantén un hervor suave a fuego medio-bajo durante 5 min., revolviendo con frecuencia. Sirve la sopa crema con galletas."}',1);
INSERT INTO `recipe` (id,name,url,attribution,duration,calories,weight,rations,difficulty,cuisine,steps,user_id) VALUES (46,'Ensalada de espaguetis','http://www.comidakraft.com/sp/recipes/ensalada-de-espaguetis-90336.aspx','Comida Kraft','25 Min',2870.9622332,2006.71474,8.0,'Difícil','Española','{"1": "Mezcla bien la mayonesa, 1/4 taza del aderezo, el ajo y el comino en un tazón grande. Agrega la pasta; mezcla todo para cubrir los ingredientes. Ponlos a un lado.", "2": "Calienta 1/4 taza de aderezo restante en un sartén grande a fuego medio-alto. Agrega el pimiento y la cebolla; cocínalos, revolviéndolos de vez en cuando, por 5 minutos o hasta que las cebollas estén tiernas. Agrega los camarones, el tomate y el pimentón; revuélvelo todo. Cocina los camarones, revolviéndolos con frecuencia, de 5 a 10 minutos o hasta que adquieran un color rosado. Retíralos del fuego.", "3": "Agrega esta mezcla a la de pasta; mezcla todo un poco. Sirve la ensalada ya sea caliente o fría."}',1);
INSERT INTO `recipe` (id,name,url,attribution,duration,calories,weight,rations,difficulty,cuisine,steps,user_id) VALUES (47,'Arroz con cerdo a la sartén','http://www.comidakraft.com/sp/recipes/arroz-con-cerdo-a-la-sarten-143386.aspx','Comida Kraft','45 Min',3826.20964,2823.313,6.0,'Difícil','Española','{"1": "Mezcla la mitad del aceite y la páprika en un tazón mediano. Agrega la carne de cerdo; revuélvela para recubrirla bien. Cocínala, revolviéndola con frecuencia, en una sartén grande a fuego medio-alto durante 5 min. o hasta que se dore. Retírala de la sartén; tápala para mantenerla caliente.", "2": "Agrega el resto del aceite a la sartén. Agrega las papas y las cebollas; cocínalas 3 min. revolviéndolas constantemente. Agrega los tomates, el ajo y el jamón; cocínalos 2 min. revolviéndolos de vez en cuando. Agrega el caldo, el agua y el hielo; hiérvelos. Incorpora los garbanzos y la carne de cerdo; pon la tapa.", "3": "Cocina el platillo a fuego bajo durante 30 min. o hasta que se absorba el líquido. Espolvoréalo con el queso antes de servir."}',1);
INSERT INTO `recipe` (id,name,url,attribution,duration,calories,weight,rations,difficulty,cuisine,steps,user_id) VALUES (48,'Lomo de puerco con hierbas finas','http://www.comidakraft.com/sp/recipes/lomo-de-puerco-con-hierbas-finas-60054.aspx','Comida Kraft','2 H 20 Min',1815.234268,1379.4088,10.0,'Difícil','Española','{"1": "Precalienta el horno a 350ºF. Quita el exceso de grasa a la carne de cerdo. Úntala parejamente con la mostaza. ", "2": "Mezcla las migajas de pan, el perejil, los cebollinos, el romero, 3 cucharadas del aceite, la sal y la pimienta en un tazón pequeño. Presiona la mezcla de migajas sobre la carne de cerdo hasta cubrirla completamente; coloca sobre la parrilla de una asadera. Mezcla las zanahorias, la cebolla y la cucharada restante de aceite. Con una cuchara, distribuye esto alrededor de la carne de puerco. ", "3": "Hornea la carne de 1 hora 30 minutos a 2 horas, o hasta que el termómetro de carne marque 160ºF al insertarlo en la parte más gruesa de la carne. Déjala reposar de 10 a 15 minutos antes de rebanarla. Sírvela con la mezcla de zanahorias."}',1);
INSERT INTO `recipe` (id,name,url,attribution,duration,calories,weight,rations,difficulty,cuisine,steps,user_id) VALUES (49,'Pizza Mediterránea','http://www.quericavida.com/recetas/pizza-mediterranea/','Que Rica Vida','30 min',1664.33,773.5,16.0,'Difícil','Mediterranea','{"1": "Calentar el horno a 400 °F. Desenrollar la masa; colocar la masa en una placa rectangular antiadherente para galletas de 15 x 10 pulgadas.", "2": "Hornear de 7 a 8 minutos o hasta que los bordes comiencen a dorarse. Colocar los ingredientes restantes en la masa parcialmente horneada y esparcir arriba el aceite de oliva.", "3": "Hornear de 6 a 9 minutos como máximo o hasta que la masa esté dorada y el queso mozzarella se haya derretido. Cortar en filas de 4x4."}',1);
INSERT INTO `recipe` (id,name,url,attribution,duration,calories,weight,rations,difficulty,cuisine,steps,user_id) VALUES (50,'Ensalada Mediterránea','http://www.quericavida.com/recetas/mini-hamburguesas-de-pavo-a-la-mostaza/','Que Rica Vida','40 min',618.474286,339.32389706,8.0,'Difícil','Mediterranea','{"1": "Pre-calienta el horno a 375°F.", "2": "En un recipiente hondo de cristal mezcla la carne, sal, pimienta, mostaza y cebolla.", "3": "Forma 8 mini hamburguesas y hornea por 20-25 minutos o hasta que la carne esté cocida por completo, cuidando de voltearlas a medio cocimiento.", "4": "Sácalas del horno y deja reposar por cinco minutos.", "5": "Coloca los bollos en una charola para hornear y hornea por 5-6 minutos hasta que estén dorados.", "6": "Corta cada bollo por la mitad, agrega mayonesa, kétchup, lechuga, jitomate y carne, y sirve inmediatamente."}',1);
INSERT INTO `recipe` (id,name,url,attribution,duration,calories,weight,rations,difficulty,cuisine,steps,user_id) VALUES (51,'Queso untable a la mediterránea','http://www.comidakraft.com/sp/recipes/queso-untable-a-la-mediterranea-116623.aspx','Comida Kraft','1 H 10 Min',2474.622875,974.4750875,3.0,'Difícil','Mediterranea','{"1": "Esparce el queso crema sobre el fondo de un plato o molde para tarta.", "2": "Mezcla los ingredientes restantes; ponlos encima del queso crema.", "3": "Sirve el queso untable con galletas RITZ Crackers."}',1);
INSERT INTO `recipe` (id,name,url,attribution,duration,calories,weight,rations,difficulty,cuisine,steps,user_id) VALUES (52,'Ensalada mediterránea de arroz','http://www.comidakraft.com/sp/recipes/ensalada-mediterranea-de-arroz-64068.aspx','Comida Kraft','3 H 20 Min',1415.864024,1274.2334,8.0,'Difícil','Mediterranea','{"1": "Mezcla todos los ingredientes, excepto el aderezo, en un recipiente grande. Luego, agrega el aderezo, revuelve ligeramente y tapa el recipiente.", "2": "Deja enfriar durante varias horas para permitir que los sabores se combinen."}',1);
INSERT INTO `recipe` (id,name,url,attribution,duration,calories,weight,rations,difficulty,cuisine,steps,user_id) VALUES (53,'Ensalada Mediterránea de Papa','http://www.quericavida.com/recetas/ensalada-mediterranea-de-papa/','Que Rica Vida','25 min',1427.4996,1072.138,12.0,'Difícil','Mediterranea','{"1": "En una cacerola u olla de 3 cuartos de galón, calienta 1 pulgada de agua hasta que hierva. Añade las papas. Tapa y calienta hasta que hierva; baja la flama. Tapa y cocina a fuego lento de 10 a 15 minutos o hasta que las papas estén blandas; escúrrelas. Deja que se enfríen un poco. Corta las papas en cubos de 3 cuartos de pulgada; colócalas en un tazón grande.", "2": "Mientras tanto, cubre un platón para horno de microondas con una toalla de papel. Coloca el tocino sobre la toalla de papel para microondas; cubre con otra toalla de papel para microondas. Hornea con temperatura alta, de 2 a 3 minutos o hasta que el tocino esté crujiente. Desmorona el tocino.", "3": "Incorpora el tocino, los tomates (jitomates), la cebolla y las aceitunas con los cubos de papa calientes. En un tazón chico, mezcla el aderezo y el vinagre; vierte la mezcla sobre las papas y revuelve con suavidad para cubrir las verduras. Espolvorea con perejil. Sirve este platillo caliente o frío."}',1);
INSERT INTO `recipe` (id,name,url,attribution,duration,calories,weight,rations,difficulty,cuisine,steps,user_id) VALUES (54,'Ensalada mediterránea de vegetales marinados','http://www.comidakraft.com/sp/recipes/ensalada-mediterranea-de-vegetales-marinados-106665.aspx','Comida Kraft','1 H 15 Min',891.819,647.55,8.0,'Difícil','Mediterranea','{"1": "Mezcla los tomates y los vegetales en una ensaladera grande.", "2": "Combina el aderezo, la albahaca y el ajo. Vierte esto sobre la mezcla de vegetales; revuélvelos para cubrirlos.", "3": "Añade el queso; mézclalo ligeramente. Refrigera la ensalada durante 1 hora."}',1);
INSERT INTO `recipe` (id,name,url,attribution,duration,calories,weight,rations,difficulty,cuisine,steps,user_id) VALUES (55,'Sopa Asiática de Puerco y Tallarines','http://www.quericavida.com/recetas/sopa-asiatica-de-puerco-y-tallarines/','Que Rica Vida','0 min',1105.9444,2225.992,5.0,'Difícil','Asiatica','{"1": "Aplica spray para cocinar a una cacerola de 3 cuartos de galón; calienta a fuego medio-alto. Añade el puerco, el ajo y la raíz de jengibre; sofríe de 3 a 5 minutos o hasta que el puerco esté dorado.", "2": "Añade el caldo, el agua y la salsa de soya. Calienta hasta que hierva; baja la flama. Cocina a fuego lento, sin tapar, por 5 minutos. Añade los tallarines, la zanahoria y el pimiento. Cocina a fuego lento, sin tapar, por unos 10 minutos o hasta que los tallarines estén blandos.", "3": "Añade la espinaca; cocina hasta que esté caliente."}',1);
INSERT INTO `recipe` (id,name,url,attribution,duration,calories,weight,rations,difficulty,cuisine,steps,user_id) VALUES (56,'Ensalada de Pollo Asiática en Hojas de Lechuga','http://www.quericavida.com/recetas/ensalada-de-pollo-asiatica-en-hojas-de-lechuga/','Que Rica Vida','15 min',1227.61975,1200.425,24.0,'Difícil','Asiatica','{"1": "En un tazón mediano, mezcla todos los ingredientes, excepto la lechuga y los cacahuates (maníes).", "2": "Vierte aproximadamente 2 cucharadas de la mezcla de pollo en cada hoja de lechuga. Espolvorea con los cacahuates (maníes)."}',1);
INSERT INTO `recipe` (id,name,url,attribution,duration,calories,weight,rations,difficulty,cuisine,steps,user_id) VALUES (57,'Thirsty Thursday: Sparkler de Pera Oriental','http://www.quericavida.com/recetas/thirsty-thursday-sparkler-de-pera-oriental/','Que Rica Vida','35 min',920.2317629,1186.14792,2.0,'Difícil','Asiatica','{"1": "En una licuadora a velocidad media, licua la pera con el agua hasta que la pera esté totalmente disuelta. Pasa la mezcla por un colador de malla bien fino. Reserva el jugo y descarta todos los restos.", "2": "En una olla pequeña a fuego medio mezcla el jugo de pera, azúcar, miel, jengibre, jugo de limón, menta y nuez moscada. Reduce el fuego y deja hervir por 5 – 8 minutos hasta que el azúcar y la miel se hayan disuelto. Retira del fuego, cuela y deja enfriar totalmente.", "3": "Llena dos vasos largos con hielo a tu gusto, agrega 8 ozs de soda en cada uno y 4 - 6 cucharadas del jarabe de pera. Mezcla bien y sirve inmediatamente."}',1);
INSERT INTO `recipe` (id,name,url,attribution,duration,calories,weight,rations,difficulty,cuisine,steps,user_id) VALUES (58,'Tiras de Pollo Asiáticas con Salsa de Limón','http://www.quericavida.com/recetas/tiras-de-pollo-asiaticas-con-salsa-de-limon/','Que Rica Vida','50 min',1040.72849,689.267,12.0,'Difícil','Asiatica','{"1": "Corta transversalmente cada pechuga de pollo en 8 tiras o rebanadas (1/2 pulgada de grueso). En un tazón hondo mediano, mezcla la salsa de soya, la miel, el jengibre y el ajo en polvo. Añade el pollo; revuelve con suavidad hasta cubrirlo. Deja reposar 20 minutos para que se marine.", "2": "Precalienta el horno a 400°F. Cubre un molde para horno de 15x10 pulgadas con papel aluminio; rocía el papel aluminio con spray para cocinar. Coloca el pan molido en un plato poco profundo. Escurre la mezcla de salsa de soya para que quede en el pollo. Empaniza las tiras de pollo con el pan molido; acomódalas en una sola capa en el molde.", "3": "Hornea 5 minutos. Voltea el pollo, hornea de 5 a 7 minutos más o hasta que esté dorado y crujiente.", "4": "En un tazón hondo chico, mezcla la salsa agridulce y la cáscara de limón. Sirve el pollo con la salsa para sumergirlo."}',1);
INSERT INTO `recipe` (id,name,url,attribution,duration,calories,weight,rations,difficulty,cuisine,steps,user_id) VALUES (59,'Pollo Satay Asado con Ensalada de Pepinos','http://www.quericavida.com/recetas/pollo-satay-asado-con-ensalada-de-pepinos/','Que Rica Vida','40 min',1423.71047921,1697.211279,4.0,'Difícil','Asiatica','{"1": "Remoja 8 brochetas de madera (10 pulgadas) en agua, 30 minutos. Precalienta la parrilla de gas o de carbón (grill). Coloca media taza de salsa de cacahuate en un tazón mediano; deja aparte. Corta el pollo transversalmente en rebanadas de media pulgada de grueso. Cubre perfectamente las rebanadas de pollo con la salsa en el tazón; espolvorea con un cuarto de cucharadita de sal. Tapa y refrigera 30 minutos.", "2": "En otro tazón hondo mediano, mezcla los ingredientes para la ensalada de pepinos; deja aparte.", "3": "Retira el pollo del tazón; desecha la salsa de cacahuate. En cada brocheta, ensarta 4 o 5 rebanadas de pollo. Coloca las brochetas en la parrilla, a fuego medio. Tapa la parrilla; cocina de 6 a 8 minutos, volteándolas una vez, hasta que el pollo ya no tenga un color rosado en el centro.", "4": "Para servir, divide la ensalada de pepinos entre 4 platos. Cubre con las brochetas de pollo y las coberturas."}',1);
INSERT INTO `recipe` (id,name,url,attribution,duration,calories,weight,rations,difficulty,cuisine,steps,user_id) VALUES (60,'Salteado de camarones y col china','http://www.comidakraft.com/sp/recipes/salteado-de-camarones-y-col-china-155158.aspx','Comida Kraft','20 Min',700.41742,656.84,4.0,'Difícil','China','{"1": "Mezcla bien los 3 primeros ingredientes.", "2": "Calienta el aceite en una sartén grande a fuego medio-alto. Agrega los pimientos y las cebollas; saltéalos 2 min. Agrega la col china; saltéala 1 min. Pon estos ingredientes en un tazón; tápalos para mantenerlos calientes.", "3": "Agrega los camarones a la sartén con 1 cda. de la mezcla de aderezo; saltéalos 2 min. Agrega los vegetales con el resto de la mezcla de aderezo; saltéalos de 1 a 2 min. o hasta que los camarones se pongan rosados y se terminen de calentar los vegetales."}',1);
INSERT INTO `recipe` (id,name,url,attribution,duration,calories,weight,rations,difficulty,cuisine,steps,user_id) VALUES (61,'Ensalada de Col China con Aderezo de Ajonjolí','http://www.quericavida.com/recetas/ensalada-de-col-china-con-aderezo-de-ajonjoli/','Que Rica Vida','15 min',228.122709,401.2499,4.0,'Difícil','China','{"1": "En un recipiente herméticamente tapado, agita todos los ingredientes para el aderezo.", "2": "En un tazón mediano de cristal o de plástico, revuelve los ingredientes para la ensalada y el aderezo. Tapa y refrigera hasta la hora de servir."}',1);
INSERT INTO `recipe` (id,name,url,attribution,duration,calories,weight,rations,difficulty,cuisine,steps,user_id) VALUES (62,'Sopa de Papas','http://www.quericavida.com/recetas/sopa-de-papas/','Que Rica Vida','30 min',2193.35592,2685.216,6.0,'Difícil','China','{"1": "Pela, lava, y corta las papas en rodajas. Ponlas a hervir en una olla con agua hasta que las papas estén suaves.", "2": "Una vez hervidas las papas retira la mayor parte del agua (no toda), asegurándote que  quede un poco en la olla. Añade el litro de leche.", "3": "Pon las papas y la leche a hervir nuevamente, y revuelve hasta que las papas comiencen a deshacerse.", "4": "Sazona al gusto. Yo utilicé 1 cucharada de sal, 2 cucharaditas de pimienta y 2 cucharaditas de ajo en polvo.", "5": "Sirve la sopa con un poco de queso cheddar rallado y cebollas verdes picadas."}',1);
